  // set state object to store array of posts
  const [posts, setPosts] = useState([testPost]);

  // add several test posts
  for(let i = 0; i < 5; i++){
    setPosts((prevPosts) => [testPost, ...prevPosts])
  }

  return (
    <div className="App">
      {posts.map(post => {
        // can pass in actual image/placeholder and text with props
        return <Post/>
      })}
    </div>
  );
}

export default App;
